mapper.insert:
        
		insert into RNO_2G_NCS_COVER_T(RNO_NCS_DESC_ID, ${ncsFields})
		  select RNO_2G_ERI_NCS_DESC_ID,  ${ncsFields}
          from RNO_2G_ERI_NCS
          where ncell = ${cell}
          and rno_2g_eri_ncs_desc_id = ${ncsDescId}
          and distance > 0.1
          and reparfcn >=5000
          and cell in (
          select label from RNO_CELL
            where indoor_cell_type = '室外覆盖'
            and area_id in(${areaIdStr})
          )

mapper.select:
        
		select * from RNO_GSM_MAP_LNGLAT_RELA_GPS
        WHERE AREAID = ${areaId}
        and MAPTYPE = ${mapType}
        ORDER BY BOTTOMLEFT

        
		select desc_id from RNO_GSM_BSC_DATA
		where id=(
		  select bsc_id from RNO_CELL
		    where label=${cell}
		    )
