mapper.select:
select t1.bsc, t1.cell, t1.nccperm, t2.ncell_ncc from (select cell.cell, d.bsc, cell.nccperm_8 as nccperm from rno_gsm_eri_cell cell left join RNO_GSM_BSC_DATA d on (d.ID = cell.bsc) where
cell.mea_date = to_date('${date1}', 'yyyy-MM-dd')
and cell.city_id = ${cityId}
and cell.bsc in (${bscStr})
) t1 left join (select cell, wm_concat(distinct(ncc)) as ncell_ncc from (select ncell.cell, ncell.n_cell, cell.ncc from (select cell, n_cell from rno_gsm_eri_ncell_param where
mea_date = to_date('${date1}', 'yyyy-MM-dd')
and city_id = ${cityId}
and bsc in (${bscStr})
) ncell left join (select cell as ncell, ncc from rno_gsm_eri_cell where
mea_date = to_date('${date1}', 'yyyy-MM-dd')
and city_id = ${cityId}
and bsc in (${bscStr})
) cell on (ncell.n_cell = cell.ncell) order by cell.ncc) group by cell ) t2 on t1.cell = t2.cell
where nccperm <> ncell_ncc

select t1.bsc, t1.cell, t1.active,t1.idle,t2.ncell_bcch from (select d.bsc, cell, active_32 as active, idle_32 as idle from rno_gsm_eri_cell cell left join RNO_GSM_BSC_DATA d on (d.ID = cell.bsc) where
cell.mea_date = to_date('${date1}', 'yyyy-MM-dd')
and cell.city_id = ${cityId}
and cell.bsc in (${bscStr})
) t1 left join (select cell, wm_concat(distinct(bcch)) as ncell_bcch from (select ncell.cell, ncell.n_cell, cell.bcchno as bcch from (select cell, n_cell from rno_gsm_eri_ncell_param where
mea_date = to_date('${date1}', 'yyyy-MM-dd')
and city_id = ${cityId}
and bsc in (${bscStr})
) ncell left join (select cell as ncell, bcchno from rno_gsm_eri_cell where
mea_date = to_date('${date1}', 'yyyy-MM-dd')
and city_id = ${cityId}
and bsc in (${bscStr})
) cell on ncell.n_cell = cell.ncell ) group by cell ) t2 on t1.cell = t2.cell

select * from (select d.bsc, cell.cell, count(cell.cell) n_cell_num, wm_concat(cell.n_cell) n_cells from rno_gsm_eri_ncell_param cell left join RNO_GSM_BSC_DATA d on (d.ID = cell.bsc) where
cell.mea_date = to_date('${date1}', 'yyyy-MM-dd')
and cell.city_id = ${cityId}
and cell.bsc in (${bscStr})
group by d.bsc, cell.cell)
where n_cell_num < ${ncellMinNum} or n_cell_num > ${ncellMaxNum}

select bsc, cell, n_cell as CELLR, cast('SINGLE'as varchar2(6)) as DIR, n_bsc as CELLR_BSC, case when bsc=n_bsc then '是' else '否' end as IS_SAME_BSC, 'RLNRC:CELLR='||n_cell||',CELL='||cell||',SINGLE;' as COMMAND from ( select t1.bsc, t1.cell, t2.n_bsc, t2.n_cell from( select d.bsc,cell.cell from rno_gsm_eri_cell cell left join RNO_GSM_BSC_DATA d on (d.ID = cell.bsc) where
cell.mea_date = to_date('${date1}', 'yyyy-MM-dd')
and cell.city_id = ${cityId}
and cell.bsc in (${bscStr})
) t1 left join ( select d.bsc,param.cell,param.n_bsc,param.n_cell from rno_gsm_eri_ncell_param param left join RNO_GSM_BSC_DATA d on (d.ID = param.bsc) where
param.mea_date = to_date('${date1}', 'yyyy-MM-dd')
and param.city_id = ${cityId}
and param.bsc in (${bscStr}) ) t2 on (t1.cell=t2.cell and t1.bsc = t2.bsc) minus select t2.n_bsc as bsc, t2.n_cell as cell, t1.bsc as n_bsc, t1.cell as ncell from( select d.bsc,cell.cell from rno_gsm_eri_cell cell left join RNO_GSM_BSC_DATA d on (d.ID = cell.bsc) where cell.mea_date = to_date('${date1}', 'yyyy-MM-dd')
and cell.city_id = ${cityId}
and cell.bsc in (${bscStr}) ) t1 left join ( select d.bsc,param.cell,param.n_bsc,param.n_cell from rno_gsm_eri_ncell_param param left join RNO_GSM_BSC_DATA d on (d.ID = param.bsc) where
param.mea_date = to_date('${date1}', 'yyyy-MM-dd')
and param.city_id = ${cityId}
and param.bsc in (${bscStr}) ) t2 on (t1.cell=t2.cell and t1.bsc = t2.bsc) ) where n_bsc is not null and n_cell is not null
